const pokemonRepository=function(){return{main:function(){pokemonRepository.getAll(),$("#creator").click(()=>{alert("Created by Darko!")})},getAll:function(){let t=[];fetch("https://pokeapi.co/api/v2/pokemon/?limit=150").then(t=>t.json()).then(e=>{(t=e.results).forEach(t=>{const e=document.querySelector(".pokemon-list"),o=document.createElement("li"),n=document.createElement("button");n.innerText=t.name,n.classList.add("btn"),n.classList.add("button-class"),n.setAttribute("data-toggle","modal"),n.setAttribute("data-target","#exampleModal"),n.addEventListener("click",()=>{fetch(`https://pokeapi.co/api/v2/pokemon/${t.name}`).then(t=>t.json()).then(e=>{const o=`Height: ${e.height}<br>`+`Weight: ${e.weight}<br>`;$(".modal-title").html(t.name),$(".modal-body").html(""),$(".modal-body").append(`${o}`,`<img src="${e.sprites.front_shiny}">`)}).catch(t=>console.error(t))}),o.appendChild(n),e.appendChild(o)})}).catch(t=>console.log(t))}}}();pokemonRepository.main();
